import com.pulumi.zpa.Inspection.inputs.GetInspectionPredefinedControlsArgs;
import com.pulumi.zpa.Inspection.InspectionProfile;
import com.pulumi.zpa.Inspection.InspectionProfileArgs;
import com.pulumi.zpa.Inspection.inputs.InspectionProfileControlsInfoArgs;
import com.pulumi.zpa.Inspection.inputs.InspectionProfilePredefinedControlArgs;
import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        final var thisInspectionAllPredefinedControls = InspectionFunctions.getInspectionAllPredefinedControls(GetInspectionAllPredefinedControlsArgs.builder()
            .version("OWASP_CRS/3.3.0")
            .groupName("Preprocessors")
            .build());

        final var thisInspectionPredefinedControls = InspectionFunctions.getInspectionPredefinedControls(GetInspectionPredefinedControlsArgs.builder()
            .name("Failed to parse request body")
            .version("OWASP_CRS/3.3.0")
            .build());

        var thisInspectionProfile = new InspectionProfile("thisInspectionProfile", InspectionProfileArgs.builder()        
            .description("Example")
            .paranoiaLevel("1")
            .predefinedControlsVersion("OWASP_CRS/3.3.0")
            .incarnationNumber("6")
            .controlsInfos(InspectionProfileControlsInfoArgs.builder()
                .controlType("PREDEFINED")
                .build())
            .dynamic(%!v(PANIC=Format method: runtime error: invalid memory address or nil pointer dereference))
            .predefinedControls(InspectionProfilePredefinedControlArgs.builder()
                .id(thisInspectionPredefinedControls.applyValue(getInspectionPredefinedControlsResult -> getInspectionPredefinedControlsResult.id()))
                .action("BLOCK")
                .build())
            .globalControlActions(            
                "PREDEFINED:PASS",
                "CUSTOM:NONE",
                "OVERRIDE_ACTION:COMMON")
            .commonGlobalOverrideActionsConfig(Map.ofEntries(
                Map.entry("PREDEF_CNTRL_GLOBAL_ACTION", "PASS"),
                Map.entry("IS_OVERRIDE_ACTION_COMMON", "TRUE")
            ))
            .build());

    }
}
