import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        final var zpnAstAuthLog = LSSConfigFunctions.getLSSLogTypeFormats(GetLSSLogTypeFormatsArgs.builder()
            .logType("zpn_ast_auth_log")
            .build());

        var example = new LSSConfigController("example", LSSConfigControllerArgs.builder()        
            .config(LSSConfigControllerConfigArgs.builder()
                .name("Example")
                .description("Example")
                .enabled(true)
                .format(zpnAstAuthLog.applyValue(getLSSLogTypeFormatsResult -> getLSSLogTypeFormatsResult.json()))
                .lssHost("splunk.acme.com")
                .lssPort("11000")
                .sourceLogType("zpn_ast_auth_log")
                .useTls(true)
                .filters(                
                    "ZPN_STATUS_AUTH_FAILED",
                    "ZPN_STATUS_DISCONNECTED",
                    "ZPN_STATUS_AUTHENTICATED")
                .build())
            .connectorGroups(LSSConfigControllerConnectorGroupArgs.builder()
                .ids(zpa_app_connector_group.example().id())
                .build())
            .build());

    }
}
