import * as zpa from "@pulumi/zpa";

const accessPolicy = zpa.PolicyType.getPolicyType({
    policyType: "ACCESS_POLICY",
});
export const zpaPolicyTypeAccessPolicy = accessPolicy.then(accessPolicy => accessPolicy.id);
